import React, { useState, useEffect, useRef } from "react";
import logo from "./assets/logo.png";

const PROXY_API_URL = "http://localhost:8090/transcribeAudio";
const SAVE_API_URL = "http://localhost:8090/saveEntry";

export default function Journal({ user, onLogout }) {
  const [entry, setEntry] = useState("");
  const [transcription, setTranscription] = useState("");
  const [isRecording, setIsRecording] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [saveMessage, setSaveMessage] = useState("");
  const [currentTimestamp, setCurrentTimestamp] = useState("");
  const [countdown, setCountdown] = useState(0);
  const [recordingDuration, setRecordingDuration] = useState(null);

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const countdownIntervalRef = useRef(null);

  const generateTimestamp = () => {
    const now = new Date();
    return `üïí ${now.toLocaleString()}`;
  };

  useEffect(() => {
    setCurrentTimestamp(generateTimestamp());
  }, []);

  useEffect(() => {
    if (!isRecording) setShowToast(false);
  }, [isRecording]);

  const startRecording = async (durationInSeconds = null) => {
    if (!navigator.mediaDevices?.getUserMedia) {
      setSaveMessage("Audio recording not supported.");
      setShowToast(true);
      return;
    }

    setCurrentTimestamp(generateTimestamp());
    setRecordingDuration(durationInSeconds);

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) audioChunksRef.current.push(event.data);
      };

      mediaRecorder.onstart = () => {
        setIsRecording(true);
        setShowToast(true);
        setSaveMessage("Recording in progress...");
        if (durationInSeconds) {
          setCountdown(durationInSeconds);
          countdownIntervalRef.current = setInterval(() => {
            setCountdown((prev) => {
              if (prev <= 1) {
                stopRecording();
                return 0;
              }
              return prev - 1;
            });
          }, 1000);
        }
      };

      mediaRecorder.onstop = async () => {
        clearInterval(countdownIntervalRef.current);
        setIsRecording(false);
        setCountdown(0);

        const audioBlob = new Blob(audioChunksRef.current, { type: "audio/webm" });
        const formData = new FormData();
        formData.append("file", audioBlob, "recording.webm");
        formData.append("user", user);

        try {
          const response = await fetch(PROXY_API_URL, {
            method: "POST",
            body: formData,
          });

          if (!response.ok) throw new Error("Transcription failed");

          const data = await response.json();
          if (data.transcription) {
            setTranscription(data.transcription);
            setSaveMessage("Transcription completed.");
          } else {
            setSaveMessage("No transcription received.");
          }
        } catch (error) {
          setSaveMessage("Transcription error: " + error.message);
        }
        setShowToast(true);
      };

      mediaRecorder.start();
    } catch (err) {
      setSaveMessage("Recording failed: " + err.message);
      setShowToast(true);
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current?.state !== "inactive") {
      mediaRecorderRef.current.stop();
    }
    clearInterval(countdownIntervalRef.current);
    setIsRecording(false);
  };

  const saveToGoogleSheet = async () => {
    try {
      const combinedText = [transcription, entry].filter(Boolean).join("\n\n");
      const response = await fetch(SAVE_API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ timestamp: currentTimestamp, entry: combinedText, user }),
      });

      if (!response.ok) throw new Error("Failed to save entry.");

      setSaveMessage("Journal entry saved!");
      setEntry("");
      setTranscription("");
      setCurrentTimestamp(generateTimestamp());
    } catch (error) {
      setSaveMessage("Save error: " + error.message);
    } finally {
      setShowToast(true);
    }
  };

  const getButtonProgress = () =>
    recordingDuration ? `${100 - (countdown / recordingDuration) * 100}%` : "0%";

  return (
    <div style={{ fontFamily: "sans-serif", padding: "2rem", backgroundColor: "#FFF8E7", maxWidth: 700, margin: "auto" }}>
      {showToast && (
        <div style={{
          position: "fixed", top: 20, left: "50%", transform: "translateX(-50%)",
          backgroundColor: "#333", color: "#fff", padding: "10px 20px", borderRadius: 20, zIndex: 999
        }}>
          {saveMessage}
        </div>
      )}

      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "1rem" }}>
        <img src={logo} alt="Logo" style={{ maxWidth: 150 }} />
        <div>
          <strong>User:</strong> {user}
          <button onClick={onLogout} style={{ marginLeft: "1rem", padding: "0.5rem 1rem", borderRadius: 6 }}>Logout</button>
        </div>
      </div>

      <div style={{ display: "flex", gap: "1rem", marginBottom: "1.5rem" }}>
        {/* Recording Buttons */}
        {[
          { label: "üî¥ Short Recording", duration: null },
          { label: "‚è±Ô∏è 1 Min", duration: 60 },
          { label: "‚è±Ô∏è 3 Min", duration: 180 },
        ].map(({ label, duration }) => (
          <button
            key={label}
            onClick={() => {
              if (isRecording) return stopRecording();
              startRecording(duration);
            }}
            disabled={isRecording && recordingDuration !== duration}
            style={{
              position: "relative",
              padding: "1rem",
              fontSize: "1rem",
              borderRadius: 8,
              border: "none",
              cursor: "pointer",
              width: "140px",
              backgroundColor: isRecording && recordingDuration === duration ? "#c0392b" : "#e67e22",
              color: "#fff",
              overflow: "hidden",
            }}
          >
            {label}
            {isRecording && recordingDuration === duration && (
              <div
                style={{
                  position: "absolute",
                  bottom: 0,
                  left: 0,
                  height: "6px",
                  background: "#27ae60",
                  width: getButtonProgress(),
                  transition: "width 1s linear",
                }}
              />
            )}
          </button>
        ))}
      </div>

      {/* Timestamp */}
      <div style={{ marginBottom: "0.5rem", fontWeight: "bold" }}>{currentTimestamp}</div>

      {/* Transcription Output */}
      {transcription && (
        <div style={{
          marginBottom: "1rem",
          padding: "1rem",
          backgroundColor: "#f0f0f0",
          border: "1px solid #ccc",
          borderRadius: "6px",
          whiteSpace: "pre-wrap",
        }}>
          <strong>üìù Transcription:</strong>
          <div>{transcription}</div>
        </div>
      )}

      {/* Manual Entry */}
      <textarea
        rows={10}
        value={entry}
        onChange={(e) => setEntry(e.target.value)}
        placeholder="Add your journal notes here..."
        style={{ width: "100%", padding: "0.75rem", borderRadius: 6, border: "1px solid #ccc", marginBottom: "1rem" }}
      />

      {/* Submit */}
      <button
        onClick={saveToGoogleSheet}
        style={{
          display: "block",
          marginLeft: "auto",
          padding: "0.75rem 2rem",
          fontSize: "1rem",
          backgroundColor: "#4CAF50",
          color: "#fff",
          border: "none",
          borderRadius: "6px",
          cursor: "pointer",
        }}
      >
        Save Entry
      </button>
    </div>
  );
}
