import React, { useState, useEffect, useRef } from "react";
import logo from "./assets/logo.png"; // Adjust path as needed

const PROXY_API_URL = "http://localhost:8090/saveEntry";
const TRANSCRIBE_API_URL = "http://localhost:8090/transcribe"; // New endpoint

export default function Journal({ user, onLogout }) {
  const [entry, setEntry] = useState("");
  const [isRecording, setIsRecording] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [saveMessage, setSaveMessage] = useState("");
  const [currentTimestamp, setCurrentTimestamp] = useState("");
  const [countdown, setCountdown] = useState(180); // 3 minutes countdown seconds

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const countdownIntervalRef = useRef(null);
  const streamRef = useRef(null);

  const generateTimestamp = () => {
    const now = new Date();
    return `ðŸ•’ ${now.toLocaleString()}`;
  };

  useEffect(() => {
    setCurrentTimestamp(generateTimestamp());
  }, []);

  const saveToGoogleSheet = async (timestamp, text, username) => {
    try {
      const response = await fetch(PROXY_API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ timestamp, entry: text, user: username }),
      });
      if (!response.ok)
        throw new Error("Failed to save entry to Google Sheets");
      return await response.json();
    } catch (error) {
      setSaveMessage("Error saving to Google Sheets: " + error.message);
      return null;
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state !== "inactive") {
      mediaRecorderRef.current.stop();
    }
    clearInterval(countdownIntervalRef.current);
    setCountdown(180);
    setIsRecording(false);
    setShowToast(false);
    setSaveMessage("Recording stopped");
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
  };

  const sendAudioForTranscription = async (audioBlob) => {
    setSaveMessage("Sending audio for transcription...");
    setShowToast(true);

    try {
      const formData = new FormData();
      formData.append("file", audioBlob, "recording.webm");

      const response = await fetch(TRANSCRIBE_API_URL, {
        method: "POST",
        body: formData,
      });

      if (!response.ok) throw new Error("Transcription failed");

      const data = await response.json();

      if (data.text) {
        setEntry((prev) => (prev ? prev + "\n" : "") + data.text);
        setSaveMessage("Transcription received.");
      } else {
        setSaveMessage("No transcription text received.");
      }
    } catch (error) {
      setSaveMessage("Error during transcription: " + error.message);
    }

    setShowToast(true);
  };

  const startRecording = async () => {
    if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
      setSaveMessage("Your browser does not support audio recording.");
      setShowToast(true);
      return;
    }

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      streamRef.current = stream;

      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.onstart = () => {
        setIsRecording(true);
        setShowToast(true);
        setSaveMessage("Recording started, speak now...");
      };

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = () => {
        setIsRecording(false);
        setShowToast(true);
        setSaveMessage("Recording stopped. Processing audio...");
        const audioBlob = new Blob(audioChunksRef.current, { type: "audio/webm" });
        sendAudioForTranscription(audioBlob);
      };

      mediaRecorder.start();

      // Start countdown timer for 180 seconds
      setCountdown(180);
      countdownIntervalRef.current = setInterval(() => {
        setCountdown((prev) => {
          if (prev <= 1) {
            stopRecording();
            setSaveMessage("Recording finished");
            setShowToast(true);
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    } catch (err) {
      setSaveMessage("Could not start recording: " + err.message);
      setShowToast(true);
    }
  };

  const handleRedButtonClick = () => {
    if (!isRecording) {
      startRecording();
    } else {
      stopRecording();
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!entry.trim()) {
      setSaveMessage("Please enter something before saving.");
      setShowToast(true);
      return;
    }
    const result = await saveToGoogleSheet(currentTimestamp, entry, user);
    if (result) {
      setSaveMessage("Journal entry saved successfully!");
      setShowToast(true);
      setEntry("");
      setCurrentTimestamp(generateTimestamp());
    } else {
      setSaveMessage("Failed to save entry. Please try again.");
      setShowToast(true);
    }
  };

  const handleRefreshTime = () => {
    setCurrentTimestamp(generateTimestamp());
    setSaveMessage("");
  };

  useEffect(() => {
    return () => {
      if (mediaRecorderRef.current && mediaRecorderRef.current.state !== "inactive") {
        mediaRecorderRef.current.stop();
      }
      clearInterval(countdownIntervalRef.current);
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
      }
    };
  }, []);

  useEffect(() => {
    if (saveMessage) {
      const timer = setTimeout(() => setSaveMessage(""), 4000);
      return () => clearTimeout(timer);
    }
  }, [saveMessage]);

  // Simple fancy countdown progress bar
  const countdownPercent = ((180 - countdown) / 180) * 100;

  return (
    <div
      style={{
        minHeight: "100vh",
        backgroundColor: "#FFF8E7",
        padding: "2rem",
        fontFamily: "sans-serif",
        maxWidth: 700,
        margin: "auto",
        color: "#222",
      }}
    >
      {/* Countdown Bar */}
      {isRecording && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "16px", // wider bar
            backgroundColor: "#d0f0d0",
            zIndex: 9999,
            boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
          }}
          aria-label="Recording countdown timer"
        >
          <div
            style={{
              width: `${countdownPercent}%`,
              height: "100%",
              background: "linear-gradient(90deg, #2ecc71, #27ae60)", // green gradient
              transition: "width 1s linear",
            }}
          />
        </div>
      )}

      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          marginBottom: "1rem",
        }}
      >
        <img
          src={logo}
          alt="My Logo"
          style={{ maxWidth: "150px", height: "auto" }}
        />
        <div>
          <strong>User:</strong> {user}
          <button
            onClick={onLogout}
            style={{
              marginLeft: "1rem",
              padding: "0.3rem 0.75rem",
              fontSize: "1rem",
              cursor: "pointer",
              borderRadius: "6px",
              border: "1px solid #888",
              backgroundColor: "#f0f0f0",
              color: "#333",
            }}
          >
            Logout
          </button>
        </div>
      </div>

      {showToast && (
        <div
          style={{
            position: "fixed",
            top: "40px",
            left: "50%",
            transform: "translateX(-50%)",
            backgroundColor: "#333",
            color: "white",
            padding: "10px 20px",
            borderRadius: "20px",
            boxShadow: "0 2px 10px rgba(0,0,0,0.7)",
            zIndex: 9999,
            fontSize: "1rem",
            opacity: 0.9,
            userSelect: "none",
          }}
          aria-live="polite"
        >
          {saveMessage || "waiting"}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div style={{ display: "flex", gap: "1.5rem" }}>
          <div
            style={{
              display: "flex",
              flexDirection: "column",
              gap: "1rem",
              flexShrink: 0,
              marginTop: "4rem",
            }}
          >
            <button
              type="button"
              onClick={handleRedButtonClick}
              style={{
                backgroundColor: "#e74c3c", // red color
                color: "white",
                fontSize: "1.5rem",
                width: "80px",
                height: "80px",
                border: "none",
                borderRadius: "50%",
                cursor: "pointer",
                boxShadow: "0 0 15px 5px rgba(231, 76, 60, 0.7)", // red glow
              }}
              title={isRecording ? "Stop recording" : "Start recording"}
            >
              {isRecording ? "Stop" : "Record"}
            </button>
          </div>

          <div style={{ flex: 1, display: "flex", flexDirection: "column" }}>
            <div
              style={{
                display: "flex",
                alignItems: "center",
                gap: "0.5rem",
                marginBottom: "0.5rem",
              }}
            >
              <input
                type="text"
                readOnly
                value={currentTimestamp}
                style={{
                  flexGrow: 1,
                  fontWeight: "bold",
                  fontSize: "1.2rem",
                  borderRadius: "6px",
                  border: "1px solid #ccc",
                  padding: "0.3rem 0.6rem",
                  backgroundColor: "#f9f9f9",
                  color: "#333",
                }}
              />
              <button
                type="button"
                onClick={handleRefreshTime}
                style={{
                  padding: "0.3rem 0.75rem",
                  fontSize: "1rem",
                  cursor: "pointer",
                  borderRadius: "6px",
                  border: "1px solid #888",
                  backgroundColor: "#f0f0f0",
                  color: "#333",
                  fontWeight: "bold",
                }}
              >
                Refresh Time
              </button>
            </div>

            <textarea
              rows={12}
              value={entry}
              onChange={(e) => setEntry(e.target.value)}
              placeholder="Type or dictate your journal entry..."
              style={{
                width: "100%",
                padding: "0.5rem",
                fontSize: "1rem",
                borderRadius: "6px",
                border: "1px solid #ccc",
                backgroundColor: "#fff",
                color: "#222",
                whiteSpace: "pre-wrap",
              }}
            />
          </div>
        </div>

        <button
