        ðŸ’¡ Click on any date to view entries below
      </div>

      <div style={{ display: "flex", justifyContent: "center" }}>
        <Calendar
          onChange={(date) => {
            console.log("Calendar date changed to:", date, "formatted as:", formatDateLocal(date));
            setSelectedDate(date);
            setShowToast(false);
            setSaveMessage("");
          }}
          value={selectedDate}
          locale="en-US"
        />
      </div>

      {/* CSV Download Section */}
      <div style={{ marginTop: "2rem", marginBottom: "2rem" }}>
        <div style={{ 
          display: "flex", 
          justifyContent: "space-between", 
          alignItems: "center", 
          marginBottom: "1rem" 
        }}>
          <h3 style={{ margin: 0, color: "#333" }}>ðŸ“¥ Download Entries as CSV</h3>
          <button
            onClick={() => setShowDownloadSection(!showDownloadSection)}
            style={{
              padding: "0.5rem 1rem",
              fontSize: "0.9rem",
              cursor: "pointer",
              borderRadius: "6px",
              border: "1px solid #888",
              backgroundColor: showDownloadSection ? "#e0e0e0" : "#2196f3",
              color: showDownloadSection ? "#333" : "white",
              fontWeight: "600"
            }}
          >
            {showDownloadSection ? "Hide Options" : "Show Download Options"}
          </button>
        </div>

        {showDownloadSection && (
          <div style={{ 
            padding: "1.5rem", 
            backgroundColor: "#f0f8ff", 
            borderRadius: "8px", 
            border: "2px solid #2196f3" 
          }}>
            <p style={{ 
              margin: "0 0 1rem 0", 
              fontSize: "0.9rem", 
              color: "#666",
              fontStyle: "italic" 
            }}>
              ðŸ“‹ Download your journal entries as a CSV file for the selected date range
            </p>

            <div style={{ 
              display: "grid", 
              gridTemplateColumns: "1fr 1fr", 
              gap: "1rem", 
              marginBottom: "1rem" 
            }}>
              <div>
                <label style={{ 
                  display: "block", 
                  marginBottom: "0.5rem", 
                  fontWeight: "bold", 
                  fontSize: "0.9rem" 
                }}>
                  ðŸ“… From Date:
                </label>
                <input
                  type="date"
                  value={downloadFromDate}
                  onChange={(e) => setDownloadFromDate(e.target.value)}
                  style={{
                    width: "100%",
                    padding: "0.5rem",
                    borderRadius: "4px",
                    border: "1px solid #ccc",
                    fontSize: "0.9rem"
                  }}
                />
              </div>

              <div>
                <label style={{ 
                  display: "block", 
                  marginBottom: "0.5rem", 
                  fontWeight: "bold", 
                  fontSize: "0.9rem" 
                }}>
                  ðŸ“… To Date:
                </label>
                <input
                  type="date"
                  value={downloadToDate}
                  onChange={(e) => setDownloadToDate(e.target.value)}
                  style={{
                    width: "100%",
                    padding: "0.5rem",
                    borderRadius: "4px",
                    border: "1px solid #ccc",
                    fontSize: "0.9rem"
                  }}
                />
              </div>
            </div>

            {downloadError && (
              <div style={{
                backgroundColor: "#fee",
                color: "#c33",
                padding: "0.75rem",
                borderRadius: "6px",
                marginBottom: "1rem",
                fontSize: "0.9rem",
                border: "1px solid #fcc",
              }}>
                âš ï¸ {downloadError}
              </div>
            )}

            <div style={{ 
              display: "flex", 
              justifyContent: "space-between", 
              alignItems: "center",
              flexWrap: "wrap",
              gap: "1rem"
            }}>
              <div style={{ fontSize: "0.8rem", color: "#666" }}>
                ðŸ‘¤ User: <strong>{user}</strong><br/>
                ðŸ“Š Range: <strong>{downloadFromDate}</strong> to <strong>{downloadToDate}</strong>
              </div>

              <button
                onClick={downloadEntriesAsCSV}
                disabled={isDownloading || !downloadFromDate || !downloadToDate}
                style={{
                  padding: "0.8rem 2rem",
                  fontSize: "1.1rem",
                  backgroundColor: isDownloading ? "#cccccc" : "#2196f3",
                  color: "white",
                  border: "none",
                  borderRadius: "8px",
                  cursor: isDownloading ? "not-allowed" : "pointer",
                  fontWeight: "700",
                  display: "flex",
                  alignItems: "center",
                  gap: "0.5rem",
                  boxShadow: isDownloading ? "none" : "0 2px 4px rgba(33, 150, 243, 0.3)"
                }}
              >
                {isDownloading ? (
                  <>
                    <span>â³</span>
                    Downloading...
                  </>
                ) : (
                  <>
                    <span>ðŸ“¥</span>
                    Download CSV
                  </>
                )}
              </button>
            </div>

            <div style={{ 
              marginTop: "1rem", 
              fontSize: "0.8rem", 
              color: "#555",
              backgroundColor: "#fff",
              padding: "0.5rem",
              borderRadius: "4px",
              border: "1px solid #ddd"
            }}>
              ðŸ’¡ <strong>CSV Format:</strong> Date, Time, Entry, User | 
              <strong>Filename:</strong> journal-entries-{user}-[daterange].csv
            </div>
          </div>
        )}
      </div>

      <div
        style={{
          marginTop: "1rem",
          padding: "1rem",
          backgroundColor: "#f9f9f9",
          borderRadius: "8px",
          border: "1px solid #ccc",
        }}
      >
        <div style={{ 
          display: "flex", 
          justifyContent: "space-between", 
          alignItems: "center",
          marginBottom: "1rem" 
        }}>
          <h3 style={{ margin: 0 }}>
            Entries for {formatDateLocal(selectedDate)}
            {isRefreshingEntries && (
              <span style={{ fontSize: "0.8rem", color: "#666", marginLeft: "1rem" }}>
                ðŸ”„ Refreshing...
              </span>
            )}
          </h3>
          <button
            onClick={handleRefreshEntries}
            disabled={isLoadingEntries || isRefreshingEntries}
            style={{
              padding: "0.4rem 0.8rem",
              fontSize: "0.8rem",
              cursor: isLoadingEntries || isRefreshingEntries ? "not-allowed" : "pointer",
              borderRadius: "6px",
              border: "1px solid #888",
              backgroundColor: "#f0f0f0",
              color: "#333",
            }}
            title="Refresh entries"
          >
            ðŸ”„ Refresh
          </button>
        </div>

        {fetchError && (
          <div
            style={{
              backgroundColor: "#fee",
              color: "#c33",
              padding: "0.75rem",
              borderRadius: "6px",
              marginBottom: "1rem",
              fontSize: "0.9rem",
              border: "1px solid #fcc",
            }}
          >
            âš ï¸ Error loading entries: {fetchError}
            {entriesForDate.length > 0 && (
              <div style={{ marginTop: "0.5rem", fontSize: "0.8rem" }}>
                Showing cached data below.
              </div>
            )}
          </div>
        )}

        {isLoadingEntries ? (
          <div style={{ 
            textAlign: "center", 
            color: "#666", 
            fontStyle: "italic",
            padding: "1rem" 
          }}>
            Loading entries...
          </div>
        ) : entriesForDate.length > 0 ? (
          <>
            <div style={{ 
              fontSize: "0.9rem", 
              color: "#666", 
              marginBottom: "1rem",
              fontStyle: "italic" 
            }}>
              ({entriesForDate.length} {entriesForDate.length === 1 ? 'entry' : 'entries'} found - Most Recent First)
              {!isCacheValid(formatDateLocal(selectedDate)) && entriesForDate.length > 0 && (
                <span style={{ color: "#f39c12", marginLeft: "0.5rem" }}>
                  (cached data)
                </span>
              )}
            </div>
            {entriesForDate.map(({ time, entry }, index) => (
              <div
                key={`${time}-${index}`}
                style={{
                  marginBottom: "1rem",
                  borderBottom: index < entriesForDate.length - 1 ? "1px solid #ddd" : "none",
                  paddingBottom: "0.5rem",
                }}
              >
                <div
                  style={{
                    fontWeight: "bold",
                    fontSize: "0.9rem",
                    marginBottom: "0.5rem",
                    color: "#555",
                    borderBottom: "1px solid #eee",
                    paddingBottom: "0.25rem",
                  }}
                >
                  {time}
                </div>
                <div style={{ 
                  whiteSpace: "pre-wrap",
                  paddingTop: "0.25rem",
                  lineHeight: "1.4"
                }}>
                  {entry}
                </div>
              </div>
            ))}
          </>
        ) : (
          <div style={{ 
            textAlign: "center", 
            color: "#666", 
            fontStyle: "italic",
            padding: "1rem" 
          }}>
            No entries found for this date.
          </div>
        )}
      </div>

      <DebugSection />
    </div>
  );
}
