import React, { useState, useEffect } from "react";

const USERS = {
  user1: "7hdxq2ma",
  user2: "n9q1zw2s",
  user3: "fmp38tkv",
  user4: "8y2aclm4",
  user5: "jd2k09qh",
  user6: "v1mw8xg0",
  user7: "43sn9vmc",
  user8: "e71r2wpq",
  user9: "wvjxy1zn",
  user10: "zhc28qrx",
  user11: "9kafj4mc",
  user12: "u8cx92rw",
  user13: "rm1txe57",
  user14: "b5gwzm41",
  user15: "70qnayhc",
  user16: "hxv9e30b",
  user17: "1vfx8qrk",
  user18: "dz48mwy1",
  user19: "4seu7bxp",
  user20: "tcgw9e3m",
};

// Function to generate random 8-character passcode
const generateRandomPasscode = () => {
  const chars = 'abcdefghijklmnopqrstuvwxyz0123456789';
  let result = '';
  for (let i = 0; i < 8; i++) {
    result += chars.charAt(Math.floor(Math.random() * chars.length));
  }
  return result;
};

export default function Login({ onLogin }) {
  const [mode, setMode] = useState("login"); // "login" or "register"
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Registration states
  const [email, setEmail] = useState("");
  const [isRegistering, setIsRegistering] = useState(false);
  const [generatedPasscode, setGeneratedPasscode] = useState("");
  const [showPasscode, setShowPasscode] = useState(false);

  // Dynamic users (backup localStorage)
  const [dynamicUsers, setDynamicUsers] = useState({});
  const [googleSheetsStatus, setGoogleSheetsStatus] = useState("unknown"); // "working", "failed", "unknown"

  // Load registered users from localStorage on component mount (backup)
  useEffect(() => {
    const stored = localStorage.getItem('registeredUsers');
    if (stored) {
      try {
        const users = JSON.parse(stored);
        console.log("üîç Loaded users from localStorage:", users);
        setDynamicUsers(users);
      } catch (e) {
        console.error("Error loading registered users:", e);
      }
    }
  }, []);

  // Save registered users to localStorage (backup)
  const saveRegisteredUsers = (users) => {
    localStorage.setItem('registeredUsers', JSON.stringify(users));
    setDynamicUsers(users);
    console.log("üíæ Saved users to localStorage:", users);
  };

  // ========================================
  // EMAIL SYSTEM FUNCTIONS
  // ========================================

  // Email service function
  const sendCredentialEmails = async (email, username, password) => {
    try {
      const SEND_EMAIL_URL = window.location.hostname === "localhost"
        ? "http://localhost:8090/send-credentials"
        : "https://journal-whisper.onrender.com/send-credentials";

      const response = await fetch(SEND_EMAIL_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          email: email,
          username: username,
          password: password
        }),
      });

      const result = await response.json();

      if (response.ok && result.success) {
        console.log("‚úÖ Credential emails scheduled successfully");
        return { success: true, message: result.message };
      } else {
        console.error("‚ùå Failed to send emails:", result.error);
        return { success: false, error: result.error };
      }

    } catch (error) {
      console.error("‚ùå Email service error:", error);
      return { success: false, error: error.message };
    }
  };

  // Function to send emails to all registered users
  const sendBulkCredentials = async () => {
    try {
      // Get users from localStorage
      const stored = localStorage.getItem('registeredUsers');
      const users = stored ? JSON.parse(stored) : {};

      // Format users for API
      const userArray = Object.entries(users).map(([username, userData]) => ({
        email: userData.email,
        username: username,
        password: userData.passcode
      }));

      if (userArray.length === 0) {
        alert("No registered users found to send emails to.");
        return;
      }

      const BULK_EMAIL_URL = window.location.hostname === "localhost"
        ? "http://localhost:8090/send-bulk-credentials"
        : "https://journal-whisper.onrender.com/send-bulk-credentials";

      console.log(`üìß Sending bulk emails to ${userArray.length} users...`);

      const response = await fetch(BULK_EMAIL_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ users: userArray }),
      });

      const result = await response.json();

      if (response.ok && result.success) {
        console.log("‚úÖ Bulk emails sent successfully:", result);
        alert(`‚úÖ Bulk emails sent to ${userArray.length} users! Each user will receive their username immediately and password in 30 seconds.`);
      } else {
        console.error("‚ùå Bulk email failed:", result);
        alert("‚ùå Bulk email failed: " + result.error);
      }

    } catch (error) {
      console.error("‚ùå Bulk email error:", error);
      alert("‚ùå Bulk email error: " + error.message);
    }
  };

  // Test email configuration
  const testEmailConfiguration = async () => {
    try {
      const TEST_EMAIL_URL = window.location.hostname === "localhost"
        ? "http://localhost:8090/test-email"
        : "https://journal-whisper.onrender.com/test-email";

      const response = await fetch(TEST_EMAIL_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
      });

      const result = await response.json();

      if (response.ok && result.success) {
        console.log("‚úÖ Email configuration test passed");
        alert("‚úÖ Email configuration is working!");
      } else {
        console.error("‚ùå Email configuration test failed:", result.error);
        alert("‚ùå Email configuration failed: " + result.error);
      }

    } catch (error) {
      console.error("‚ùå Email test error:", error);
      alert("‚ùå Email test error: " + error.message);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    console.log("üîç Attempting login with username:", username);

    // Step 1: Check hard-coded users first (fastest)
    if (USERS[username] && USERS[username] === password) {
      console.log("‚úÖ Login successful with hard-coded user");
      setError("");
      onLogin(username);
      return;
    }

    // Step 2: Check localStorage registered users (fast backup)
    if (dynamicUsers[username] && dynamicUsers[username].passcode === password) {
      console.log("‚úÖ Login successful with localStorage user");
      setError("");
      onLogin(username);
      return;
    }

    // Step 3: Check Google Sheets for registered users (if backend configured)
    try {
      console.log("üîç Checking Google Sheets for user credentials...");

      const LOGIN_CHECK_URL =
        window.location.hostname === "localhost"
          ? "http://localhost:8090/checkUser"
          : "https://journal-whisper.onrender.com/checkUser";

      const response = await fetch(LOGIN_CHECK_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          username: username,
          passcode: password
        }),
      });

      if (response.ok) {
        const result = await response.json();
        if (result.success) {
          console.log("‚úÖ Login successful with Google Sheets user");
          setGoogleSheetsStatus("working");
          setError("");
          onLogin(username);
          return;
        } else {
          console.log("‚ùå Invalid credentials in Google Sheets");
          setGoogleSheetsStatus("working");
        }
      } else {
        console.log("‚ö†Ô∏è Google Sheets backend error:", response.status);
        setGoogleSheetsStatus("failed");
      }

    } catch (error) {
      console.log("‚ö†Ô∏è Backend login check failed:", error.message);
      setGoogleSheetsStatus("failed");
    }

    setError("Invalid username or password");
  };

  // UPDATED: Registration function with email system integration
  const handleRegister = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    // Basic validation
    if (!email) {
      setError("Email is required");
      return;
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setError("Please enter a valid email address");
      return;
    }

    const existingUser = Object.keys(dynamicUsers).find(user => 
      dynamicUsers[user].email === email
    );
    if (existingUser) {
      setError("Email already registered in local storage");
      return;
    }

    setIsRegistering(true);

    try {
      const timestamp = Date.now().toString().slice(-4);
      const randomPrefix = Math.random().toString(36).substring(2, 6);
      const newUsername = `user_${randomPrefix}${timestamp}`;
      const newPasscode = generateRandomPasscode();

      console.log("üéØ Generated credentials:", { username: newUsername, passcode: newPasscode });

      let backendSuccess = false;
      let backendError = null;

      // Try to save to Google Sheets backend first
      try {
        const REGISTER_API_URL = window.location.hostname === "localhost"
          ? "http://localhost:8090/register"
          : "https://journal-whisper.onrender.com/register";

        console.log("üì§ Sending registration to Google Sheets...");

        const response = await fetch(REGISTER_API_URL, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            username: newUsername,
            password: newPasscode,
            email: email,
            // REMOVED: fullName: email, (no longer needed)
            registrationDate: new Date().toISOString(),
          }),
        });

        if (response.ok) {
          const result = await response.json();
          if (result.success) {
            console.log("‚úÖ User saved to Google Sheets successfully");
            setGoogleSheetsStatus("working");
            backendSuccess = true;
          } else {
            console.log("‚ùå Google Sheets registration failed:", result.error);
            backendError = result.error;
            setGoogleSheetsStatus("working");

            if (result.error.includes("already exists") || result.error.includes("already registered")) {
              setError(result.error);
              return;
            }
          }
        } else {
          const errorData = await response.json().catch(() => ({ error: "Unknown error" }));
          console.log("‚ùå Backend registration failed with status:", response.status);
          backendError = errorData.error || `Server error: ${response.status}`;
          setGoogleSheetsStatus("failed");
        }
      } catch (backendErrorCatch) {
        console.log("‚ö†Ô∏è Backend not available for registration:", backendErrorCatch.message);
        setGoogleSheetsStatus("failed");
        backendError = "Backend service unavailable";
      }

      // Always save to localStorage as backup
      const updatedUsers = {
        ...dynamicUsers,
        [newUsername]: {
          passcode: newPasscode,
          email: email,
          registrationDate: new Date().toISOString()
        }
      };

      saveRegisteredUsers(updatedUsers);

      // üÜï NEW: Try to send credential emails
      console.log("üìß Attempting to send credential emails...");
      const emailResult = await sendCredentialEmails(email, newUsername, newPasscode);

      if (emailResult.success) {
        // ‚úÖ Email sent successfully - don't show credentials on screen for security
        setSuccess(`‚úÖ Account created successfully! Your username and password have been sent to ${email} in separate emails. Check your inbox now!`);
        setEmail("");

        // Don't show credentials on screen for security
        setShowPasscode(false);
        setGeneratedPasscode("");
        setUsername("");

      } else {
        // ‚ùå Email failed - show credentials on screen as backup
        console.log("‚ùå Email sending failed, showing credentials on screen");
        setGeneratedPasscode(newPasscode);
        setUsername(newUsername);
        setShowPasscode(true);

        let storageMethod;
        if (backendSuccess) {
          storageMethod = "Google Sheets and localStorage backup";
        } else {
          storageMethod = `localStorage only (${backendError || "backend unavailable"})`;
        }

        setSuccess(`‚úÖ Account created and saved to ${storageMethod}. ‚ö†Ô∏è Email sending failed (${emailResult.error}), so please save the credentials shown below.`);
        setEmail("");
      }

      console.log("‚úÖ Registration completed successfully");

    } catch (error) {
      console.error("Registration error:", error);
      setError("Registration failed: " + error.message);
    } finally {
      setIsRegistering(false);
    }
  };

  const switchMode = () => {
    setMode(mode === "login" ? "register" : "login");
    setError("");
    setSuccess("");
    setUsername("");
    setPassword("");
    setEmail("");
    setGeneratedPasscode("");
    setShowPasscode(false);
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text).then(() => {
      alert("Copied to clipboard!");
    }).catch(() => {
      alert("Please manually copy the text");
    });
  };

  // Get status indicator for Google Sheets
  const getBackendStatusIndicator = () => {
    switch(googleSheetsStatus) {
      case "working":
        return "üü¢ Google Sheets Connected";
      case "failed":
        return "üü° Using Local Storage Only";
      default:
        return "‚ö™ Backend Status Unknown";
    }
  };

  // Admin Panel Component for email testing
  const AdminPanel = () => (
    <div style={{
      marginTop: "1rem",
      padding: "1rem",
      backgroundColor: "#f9f9f9",
      borderRadius: "6px",
      fontSize: "0.85rem"
    }}>
      <div style={{ fontWeight: "bold", marginBottom: "0.5rem" }}>
        üîß Admin Tools:
      </div>
      <button
        onClick={testEmailConfiguration}
        style={{
          margin: "0.25rem",
          padding: "0.5rem 1rem",
          backgroundColor: "#2196f3",
          color: "white",
          border: "none",
          borderRadius: "4px",
          cursor: "pointer",
          fontSize: "0.8rem"
        }}
      >
        Test Email Config
      </button>
      <button
        onClick={sendBulkCredentials}
        style={{
          margin: "0.25rem",
          padding: "0.5rem 1rem",
          backgroundColor: "#ff9800",
          color: "white",
          border: "none",
          borderRadius: "4px",
          cursor: "pointer",
          fontSize: "0.8rem"
        }}
      >
        Send Bulk Emails to All Users
      </button>
    </div>
  );

  return (
    <div
      style={{
        maxWidth: 420,
        margin: "3rem auto",
        padding: "2rem",
        fontFamily: "sans-serif",
        backgroundColor: "#fff",
        borderRadius: "12px",
        boxShadow: "0 4px 20px rgba(0,0,0,0.1)",
      }}
    >
      <h2 style={{ textAlign: "center", marginBottom: "2rem", color: "#333" }}>
        {mode === "login" ? "üîê Journal Login" : "üìù Create New Account"}
      </h2>

      {/* Backend Status Indicator */}
      {googleSheetsStatus !== "unknown" && (
        <div style={{
          textAlign: "center",
          fontSize: "0.8rem",
          marginBottom: "1rem",
          padding: "0.5rem",
          backgroundColor: googleSheetsStatus === "working" ? "#e8f5e8" : "#fff3cd",
          borderRadius: "4px",
          border: `1px solid ${googleSheetsStatus === "working" ? "#c8e6c9" : "#ffeaa7"}`
        }}>
          {getBackendStatusIndicator()}
        </div>
      )}

      {mode === "login" ? (
        // LOGIN FORM
        <form onSubmit={handleLogin}>
          <div style={{ marginBottom: "1rem" }}>
            <label style={{ display: "block", marginBottom: "0.5rem", fontWeight: "bold" }}>
              Username:
            </label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              autoComplete="username"
              required
              style={{
                width: "100%",
                padding: "0.75rem",
                borderRadius: "6px",
                border: "1px solid #ddd",
                fontSize: "1rem",
                boxSizing: "border-box",
              }}
              placeholder="Enter your username"
            />
          </div>

          <div style={{ marginBottom: "1.5rem" }}>
            <label style={{ display: "block", marginBottom: "0.5rem", fontWeight: "bold" }}>
              Password/Passcode:
            </label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              autoComplete="current-password"
              required
              style={{
                width: "100%",
                padding: "0.75rem",
                borderRadius: "6px",
                border: "1px solid #ddd",
                fontSize: "1rem",
                boxSizing: "border-box",
              }}
              placeholder="Enter your password/passcode"
            />
          </div>

          <button
            type="submit"
            style={{
              width: "100%",
              padding: "0.75rem",
              backgroundColor: "#4CAF50",
              color: "white",
              border: "none",
              fontSize: "1rem",
              cursor: "pointer",
              borderRadius: "6px",
              fontWeight: "bold",
              marginBottom: "1rem",
            }}
          >
            Login
          </button>
        </form>
      ) : (
        // REGISTRATION FORM
        <form onSubmit={handleRegister}>
          <div style={{ marginBottom: "1.5rem" }}>
            <label style={{ display: "block", marginBottom: "0.5rem", fontWeight: "bold" }}>
              Email Address:
            </label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              style={{
                width: "100%",
                padding: "0.75rem",
                borderRadius: "6px",
                border: "1px solid #ddd",
                fontSize: "1rem",
                boxSizing: "border-box",
              }}
              placeholder="Enter your email address"
            />
            <div style={{ fontSize: "0.8rem", color: "#666", marginTop: "0.3rem" }}>
              We'll generate a username and passcode for you
            </div>
          </div>

          <button
            type="submit"
            disabled={isRegistering}
            style={{
              width: "100%",
              padding: "0.75rem",
              backgroundColor: isRegistering ? "#cccccc" : "#2196f3",
              color: "white",
              border: "none",
              fontSize: "1rem",
              cursor: isRegistering ? "not-allowed" : "pointer",
              borderRadius: "6px",
              fontWeight: "bold",
              marginBottom: "1rem",
            }}
          >
            {isRegistering ? "Creating Account..." : "Create Account"}
          </button>
        </form>
      )}

      {/* Generated Credentials Display */}
      {showPasscode && generatedPasscode && (
        <div style={{
          backgroundColor: "#e8f5e8",
          border: "2px solid #4CAF50",
          borderRadius: "8px",
          padding: "1.5rem",
          marginBottom: "1rem",
        }}>
          <h3 style={{ margin: "0 0 1rem 0", color: "#2e7d32", textAlign: "center" }}>
            üéâ Account Created Successfully!
          </h3>

          <div style={{ marginBottom: "1rem" }}>
            <strong>Username:</strong>
            <div style={{ 
              backgroundColor: "white", 
              padding: "0.5rem", 
              borderRadius: "4px", 
              fontFamily: "monospace",
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              marginTop: "0.3rem"
            }}>
              <span>{username}</span>
              <button 
                onClick={() => copyToClipboard(username)}
                style={{
                  background: "none",
                  border: "1px solid #ccc",
                  borderRadius: "4px",
                  padding: "0.2rem 0.5rem",
                  cursor: "pointer",
                  fontSize: "0.8rem"
                }}
              >
                Copy
              </button>
            </div>
          </div>

          <div style={{ marginBottom: "1rem" }}>
            <strong>Passcode:</strong>
            <div style={{ 
              backgroundColor: "white", 
              padding: "0.5rem", 
              borderRadius: "4px", 
              fontFamily: "monospace",
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              marginTop: "0.3rem"
            }}>
              <span style={{ fontSize: "1.1rem", fontWeight: "bold" }}>{generatedPasscode}</span>
              <button 
                onClick={() => copyToClipboard(generatedPasscode)}
                style={{
                  background: "none",
                  border: "1px solid #ccc",
                  borderRadius: "4px",
                  padding: "0.2rem 0.5rem",
                  cursor: "pointer",
                  fontSize: "0.8rem"
                }}
              >
                Copy
              </button>
            </div>
          </div>

          <div style={{ 
            fontSize: "0.85rem", 
            color: "#d32f2f", 
            backgroundColor: "#ffebee",
            padding: "0.75rem",
            borderRadius: "4px",
            border: "1px solid #ffcdd2",
            textAlign: "center"
          }}>
            ‚ö†Ô∏è <strong>IMPORTANT:</strong> Save your username and passcode now! 
            <br/>You'll need them to login to your journal.
            <br/><strong>Email sending failed - these credentials are shown as backup.</strong>
          </div>

          <button
            onClick={() => {
              setMode("login");
              setShowPasscode(false);
              setPassword("");
            }}
            style={{
              width: "100%",
              padding: "0.75rem",
              backgroundColor: "#4CAF50",
              color: "white",
              border: "none",
              fontSize: "1rem",
              cursor: "pointer",
              borderRadius: "6px",
              fontWeight: "bold",
              marginTop: "1rem",
            }}
          >
            Continue to Login
          </button>
        </div>
      )}

      {/* Error and Success Messages */}
      {error && (
        <div style={{ 
          color: "#d32f2f", 
          marginBottom: "1rem", 
          textAlign: "center",
          backgroundColor: "#ffebee",
          padding: "0.75rem",
          borderRadius: "6px",
          border: "1px solid #ffcdd2"
        }}>
          ‚ö†Ô∏è {error}
        </div>
      )}

      {success && !showPasscode && (
        <div style={{ 
          color: "#2e7d32", 
          marginBottom: "1rem", 
          textAlign: "center",
          backgroundColor: "#e8f5e8",
          padding: "0.75rem",
          borderRadius: "6px",
          border: "1px solid #c8e6c9"
        }}>
          ‚úÖ {success}
        </div>
      )}

      {/* Mode Switch Button */}
      {!showPasscode && (
        <div style={{ textAlign: "center", marginTop: "1rem" }}>
          <button
            onClick={switchMode}
            style={{
              background: "none",
              border: "none",
              color: "#2196f3",
              cursor: "pointer",
              textDecoration: "underline",
              fontSize: "0.9rem",
            }}
          >
            {mode === "login" 
              ? "New user? Create account with email" 
              : "Have an account? Login here"}
          </button>
        </div>
      )}

      {/* Enhanced Debug Info */}
      <div style={{
        marginTop: "2rem",
        padding: "1rem",
        backgroundColor: "#f0f0f0",
        borderRadius: "6px",
        fontSize: "0.8rem",
        color: "#666"
      }}>
        <div style={{ fontWeight: "bold", marginBottom: "0.5rem" }}>
          üîç System Status:
        </div>
        <div>
          Hard-coded users: {Object.keys(USERS).length}<br/>
          Registered users: {Object.keys(dynamicUsers).length}<br/>
          Backend status: {getBackendStatusIndicator()}<br/>
          {Object.keys(dynamicUsers).length > 0 && (
            <div>Last registered: {Object.keys(dynamicUsers)[Object.keys(dynamicUsers).length - 1]}</div>
          )}
        </div>
      </div>

      {/* Login Method Info */}
      <div style={{
        marginTop: "1rem",
        padding: "1rem",
        backgroundColor: "#f5f5f5",
        borderRadius: "6px",
        fontSize: "0.85rem",
        color: "#666"
      }}>
        <div style={{ fontWeight: "bold", marginBottom: "0.5rem" }}>
          üîê Login Methods:
        </div>
        <div>
          1. <strong>Hard-coded users</strong> (user1-user20)<br/>
          2. <strong>Local registered users</strong> (this browser only)<br/>
          3. <strong>Google Sheets users</strong> (accessible anywhere)
          {googleSheetsStatus === "failed" && (
            <div style={{ color: "#d32f2f", marginTop: "0.5rem" }}>
              ‚ö†Ô∏è Google Sheets backend not available - using local storage only
            </div>
          )}
        </div>
      </div>

      {/* NEW: Admin Panel for Email Testing */}
      <AdminPanel />
    </div>
  );
}
