import React, { useState, useEffect, useRef } from "react";
import logo from "./assets/logo.png"; // Adjust path as needed

const PROXY_API_URL = "http://localhost:8090/transcribeAudio"; // backend endpoint to receive audio & transcribe

export default function Journal({ user, onLogout }) {
  const [entry, setEntry] = useState("");
  const [isRecording, setIsRecording] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [saveMessage, setSaveMessage] = useState("");
  const [currentTimestamp, setCurrentTimestamp] = useState("");
  const [countdown, setCountdown] = useState(0); // current countdown seconds left
  const [recordingDuration, setRecordingDuration] = useState(0); // total duration for current recording in seconds

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const countdownIntervalRef = useRef(null);

  const generateTimestamp = () => {
    const now = new Date();
    return `ðŸ•’ ${now.toLocaleString()}`;
  };

  useEffect(() => {
    setCurrentTimestamp(generateTimestamp());
  }, []);

  // Start recording with specified duration in seconds
  const startRecording = async (durationSec) => {
    if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
      setSaveMessage("Audio recording not supported in this browser.");
      setShowToast(true);
      return;
    }
    try {
      setCurrentTimestamp(generateTimestamp()); // Refresh timestamp when recording starts
      setRecordingDuration(durationSec);
      setCountdown(durationSec);
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstart = () => {
        setIsRecording(true);
        setShowToast(true);
        setSaveMessage("Recording in progress...");
      };

      mediaRecorder.onstop = async () => {
        clearInterval(countdownIntervalRef.current);
        setCountdown(0);
        setIsRecording(false);
        setSaveMessage("Processing audio...");
        setShowToast(true);

        const audioBlob = new Blob(audioChunksRef.current, { type: "audio/webm" });
        try {
          const formData = new FormData();
          formData.append("file", audioBlob, "recording.webm");
          formData.append("user", user);

          const response = await fetch(PROXY_API_URL, {
            method: "POST",
            body: formData,
          });

          if (!response.ok) {
            throw new Error(`Transcription failed: ${response.statusText}`);
          }

          const data = await response.json();

          if (data.transcription) {
            // Show transcription as toast message (not in textarea)
            setSaveMessage(`Transcription: ${data.transcription}`);

            // Append transcription to text box
            setEntry((prev) => (prev ? prev + "\n" : "") + data.transcription);

            // Save transcription + timestamp + user to Google Sheets
            await saveToGoogleSheet(currentTimestamp, data.transcription, user);
          } else {
            setSaveMessage("No transcription received.");
          }
        } catch (error) {
          setSaveMessage("Error during transcription: " + error.message);
        }
      };

      mediaRecorder.start();

      // Setup countdown interval for progress bar & auto-stop
      countdownIntervalRef.current = setInterval(() => {
        setCountdown((prev) => {
          if (prev <= 1) {
            stopRecording();
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    } catch (err) {
      setSaveMessage("Could not start recording: " + err.message);
      setShowToast(true);
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state !== "inactive") {
      mediaRecorderRef.current.stop();
    }
    clearInterval(countdownIntervalRef.current);
    setIsRecording(false);
    setCountdown(0);
    setSaveMessage(""); // clear "Recording in progress..." message on stop
  };

  const saveToGoogleSheet = async (timestamp, text, username) => {
    try {
      const response = await fetch("http://localhost:8090/saveEntry", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ timestamp, entry: text, user: username }),
      });
      if (!response.ok) throw new Error("Failed to save entry to Google Sheets");
      return await response.json();
    } catch (error) {
      setSaveMessage("Error saving to Google Sheets: " + error.message);
      setShowToast(true);
      return null;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!entry.trim()) {
      setSaveMessage("Please enter something before saving.");
      setShowToast(true);
      return;
    }
    const result = await saveToGoogleSheet(currentTimestamp, entry, user);
    if (result) {
      setSaveMessage("Journal entry saved successfully!");
      setShowToast(true);
      setEntry("");
      setCurrentTimestamp(generateTimestamp());
    } else {
      setSaveMessage("Failed to save entry. Please try again.");
      setShowToast(true);
    }
  };

  const handleRefreshTime = () => {
    setCurrentTimestamp(generateTimestamp());
    setSaveMessage("");
  };

  useEffect(() => {
    return () => {
      if (mediaRecorderRef.current && mediaRecorderRef.current.state !== "inactive") {
        mediaRecorderRef.current.stop();
      }
      clearInterval(countdownIntervalRef.current);
    };
  }, []);

  useEffect(() => {
    if (saveMessage) {
      const timer = setTimeout(() => setSaveMessage(""), 5000);
      return () => clearTimeout(timer);
    }
  }, [saveMessage]);

  // Calculate progress bar width percent for the active button
  const getButtonProgress = () => {
    if (!isRecording || countdown === 0 || recordingDuration === 0) return "0%";
    const elapsed = recordingDuration - countdown;
    return `${(elapsed / recordingDuration) * 100}%`;
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        backgroundColor: "#FFF8E7",
        padding: "2rem",
        fontFamily: "sans-serif",
        maxWidth: 700,
        margin: "auto",
        color: "#222",
      }}
    >
      {showToast && saveMessage && (
        <div
          style={{
            position: "fixed",
            top: "40px",
            left: "50%",
            transform: "translateX(-50%)",
            backgroundColor: "#333",
            color: "white",
            padding: "10px 20px",
            borderRadius: "20px",
            boxShadow: "0 2px 10px rgba(0,0,0,0.7)",
            zIndex: 9999,
            fontSize: "1rem",
            opacity: 0.9,
            userSelect: "none",
          }}
          aria-live="polite"
        >
          {saveMessage}
        </div>
      )}

      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          marginBottom: "1rem",
        }}
      >
        <img
          src={logo}
          alt="My Logo"
          style={{ maxWidth: "150px", height: "auto" }}
        />
        <div>
          <strong>User:</strong> {user}
          <button
            onClick={onLogout}
            style={{
              marginLeft: "1rem",
              padding: "0.3rem 0.75rem",
              fontSize: "1rem",
              cursor: "pointer",
              borderRadius: "6px",
              border: "1px solid #888",
              backgroundColor: "#f0f0f0",
              color: "#333",
            }}
          >
            Logout
          </button>
        </div>
      </div>

      <div
        style={{
          display: "flex",
          gap: "1rem",
          marginBottom: "1rem",
          justifyContent: "center",
        }}
      >
        {[
          { label: "ðŸ”´ Short Recording", duration: 30 },
          { label: "â±ï¸ 1 Min", duration: 60 },
          { label: "â±ï¸ 3 Min", duration: 180 },
        ].map(({ label, duration }) => (
          <button
            key={label}
            onClick={() => {
              if (isRecording) return stopRecording();
              startRecording(duration);
            }}
            disabled={isRecording && recordingDuration !== duration}
            style={{
              position: "relative",
              padding: "1rem",
              fontSize: "1rem",
              borderRadius: 8,
              border: "none",
              cursor: isRecording && recordingDuration !== duration ? "not-allowed" : "pointer",
              width: "140px",
              backgroundColor: isRecording && recordingDuration === duration ? "#FFB300" : "#FFD700", // bright yellow and darker yellow for active
              color: "#000",
              overflow: "hidden",
              userSelect: "none",
            }}
          >
            {label}
            {isRecording && recordingDuration === duration && (
              <div
                style={{
                  position: "absolute",
                  bottom: 0,
                  left: 0,
                  height: "6px",
                  background: "#27ae60",
                  width: getButtonProgress(),
                  transition: "width 1s linear",
                }}
              />
            )}
          </button>
        ))}
      </div>

      <form onSubmit={handleSubmit}>
        <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "0.3rem" }}>
          <span>{currentTimestamp}</span>
          <button
            type="button"
            onClick={handleRefreshTime}
            style={{
              backgroundColor: "#666",
              color: "#fff",
              border: "none",
              borderRadius: "6px",
              padding: "0.3rem 0.75rem",
              cursor: "pointer",
            }}
            title="Refresh timestamp"
          >
            Refresh Time
          </button>
        </div>
        <textarea
          rows={10}
          style={{
            width: "100%",
            borderRadius: "10px",
            border: "2px solid #ccc",
            padding: "1rem",
            fontSize: "1rem",
            resize: "vertical",
            fontFamily: "monospace",
          }}
          placeholder="Write your journal entry here or record..."
          value={entry}
          onChange={(e) => setEntry(e.target.value)}
          disabled={isRecording}
          aria-label="Journal entry text area"
        />
        <button
          type="submit"
          disabled={isRecording}
          style={{
            marginTop: "1rem",
            padding: "0.75rem 1.5rem",
            fontSize: "1.1rem",
            cursor: isRecording ? "not-allowed" : "pointer",
            borderRadius: "8px",
            border: "none",
            backgroundColor: "#27ae60",
            color: "white",
            fontWeight: "bold",
            width: "100%",
          }}
        >
          Save Entry
        </button>
      </form>
    </div>
  );
}
