import React, { useState, useEffect, useRef } from "react";
import logo from "./assets/logo.png"; // Adjust path as needed

const PROXY_API_URL = "http://localhost:8090/saveEntry";

export default function Journal({ user, onLogout }) {
  const [entry, setEntry] = useState("");
  const [isRecording, setIsRecording] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [saveMessage, setSaveMessage] = useState("");
  const [currentTimestamp, setCurrentTimestamp] = useState("");

  const recognitionRef = useRef(null);
  const recordingStartTimeRef = useRef(null);
  const elapsedRecordingTimeRef = useRef(0);
  const stopTimeoutRef = useRef(null);

  const MAX_RECORDING_TIME = 15000; // 15 seconds max recording

  const generateTimestamp = () => {
    const now = new Date();
    return `ðŸ•’ ${now.toLocaleString()}`;
  };

  useEffect(() => {
    setCurrentTimestamp(generateTimestamp());
  }, []);

  // The added saveToGoogleSheet function
  const saveToGoogleSheet = async (timestamp, text, username) => {
    try {
      const response = await fetch(PROXY_API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ timestamp, entry: text, user: username }),
      });
      if (!response.ok)
        throw new Error("Failed to save entry to Google Sheets");
      return await response.json();
    } catch (error) {
      setSaveMessage("Error saving to Google Sheets: " + error.message);
      return null;
    }
  };

  const startRecognition = () => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      setSaveMessage("Voice recognition not supported in this browser.");
      return;
    }

    if (!recognitionRef.current) {
      const recognition = new SpeechRecognition();
      recognition.lang = "en-US";

      // CHANGE: continuous set to true to keep listening through pauses
      recognition.continuous = true;
      recognition.interimResults = false;

      recognition.onstart = () => {
        setIsRecording(true);
        setShowToast(true);
        recordingStartTimeRef.current = Date.now();
      };

      recognition.onresult = (event) => {
        // CHANGE: get last result from event.results
        const transcript = event.results[event.results.length - 1][0].transcript;
        setEntry((prev) => (prev ? prev + "\n" : "") + transcript);
      };

      recognition.onerror = (event) => {
        setSaveMessage("Recognition error: " + event.error);
        resetRecording();
      };

      recognition.onend = () => {
        if (isRecording && !isPaused) {
          // CHANGE: restart recognition to keep it going indefinitely
          recognition.start();
          recordingStartTimeRef.current = Date.now();
        }
      };

      recognitionRef.current = recognition;
    }

    recognitionRef.current.start();

    stopTimeoutRef.current = setTimeout(() => {
      if (recognitionRef.current) recognitionRef.current.stop();
    }, MAX_RECORDING_TIME - elapsedRecordingTimeRef.current);
  };

  const resetRecording = () => {
    if (recognitionRef.current) recognitionRef.current.stop();
    setIsRecording(false);
    setIsPaused(false);
    setShowToast(false);
    elapsedRecordingTimeRef.current = 0;
    clearTimeout(stopTimeoutRef.current);
  };

  const handleRedButtonClick = () => {
    if (!isRecording) {
      elapsedRecordingTimeRef.current = 0;
      setIsPaused(false);
      startRecognition();
    } else {
      resetRecording();
    }
  };

  const handleYellowButtonClick = () => {
    if (!isRecording) return;

    if (!isPaused) {
      if (recognitionRef.current) recognitionRef.current.stop();
      setIsPaused(true);
      setShowToast(false);
      clearTimeout(stopTimeoutRef.current);
    } else {
      setIsPaused(false);
      startRecognition();
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!entry.trim()) {
      setSaveMessage("Please enter something before saving.");
      return;
    }
    const result = await saveToGoogleSheet(currentTimestamp, entry, user);
    if (result) {
      setSaveMessage("Journal entry saved successfully!");
      setEntry("");
      setCurrentTimestamp(generateTimestamp());
    } else {
      setSaveMessage("Failed to save entry. Please try again.");
    }
  };

  const handleRefreshTime = () => {
    setCurrentTimestamp(generateTimestamp());
    setSaveMessage("");
  };

  useEffect(() => {
    return () => {
      if (recognitionRef.current) recognitionRef.current.stop();
      clearTimeout(stopTimeoutRef.current);
    };
  }, []);

  useEffect(() => {
    if (showToast) {
      const timer = setTimeout(() => setShowToast(false), 3000);
      return () => clearTimeout(timer);
    }
  }, [showToast]);

  useEffect(() => {
    if (saveMessage) {
      const timer = setTimeout(() => setSaveMessage(""), 4000);
      return () => clearTimeout(timer);
    }
  }, [saveMessage]);

  return (
    <div
      style={{
        minHeight: "100vh",
        backgroundColor: "#FFF8E7",
        padding: "2rem",
        fontFamily: "sans-serif",
        maxWidth: 700,
        margin: "auto",
        color: "#222",
      }}
    >
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          marginBottom: "1rem",
        }}
      >
        <img
          src={logo}
          alt="My Logo"
          style={{ maxWidth: "150px", height: "auto" }}
        />
        <div>
          <strong>User:</strong> {user}
          <button
            onClick={onLogout}
            style={{
              marginLeft: "1rem",
              padding: "0.3rem 0.75rem",
              fontSize: "1rem",
              cursor: "pointer",
              borderRadius: "6px",
              border: "1px solid #888",
              backgroundColor: "#f0f0f0",
              color: "#333",
            }}
          >
            Logout
          </button>
        </div>
      </div>

      <h1 style={{ textAlign: "center", marginBottom: "2rem" }}>My Journal</h1>

      {showToast && (
        <div
          style={{
            position: "fixed",
            top: "20px",
            left: "50%",
            transform: "translateX(-50%)",
            backgroundColor: "#333",
            color: "white",
            padding: "10px 20px",
            borderRadius: "20px",
            boxShadow: "0 2px 10px rgba(0,0,0,0.7)",
            zIndex: 9999,
            fontSize: "1rem",
            opacity: 0.9,
          }}
        >
          I am listening, please go ahead
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div style={{ display: "flex", gap: "1.5rem" }}>
          <div
            style={{
              display: "flex",
              flexDirection: "column",
              gap: "1rem",
              flexShrink: 0,
              marginTop: "4rem",
            }}
          >
            <button
              type="button"
              onClick={handleRedButtonClick}
              style={{
                backgroundColor: "#cc0000",
                color: "white",
                fontSize: "1.5rem",
                width: "80px",
                height: "80px",
                border: "none",
                borderRadius: "50%",
                cursor: "pointer",
                boxShadow: "0 0 15px 5px rgba(204, 0, 0, 0.7)",
              }}
              title={isRecording ? "Stop recording" : "Start recording"}
            >
              {isRecording ? "Stop" : "Record"}
            </button>

            <button
              type="button"
              onClick={handleYellowButtonClick}
              disabled={!isRecording}
              style={{
                backgroundColor: "#FFD700",
                color: "#333",
                fontSize: "1.3rem",
                width: "80px",
                height: "80px",
                border: "none",
                borderRadius: "50%",
                cursor: isRecording ? "pointer" : "not-allowed",
                boxShadow: isRecording
                  ? "0 0 15px 5px rgba(255, 215, 0, 0.7)"
                  : "none",
              }}
              title={isPaused ? "Continue recording" : "Pause recording"}
            >
              {isPaused ? "Continue" : "Pause"}
            </button>
          </div>

          <div style={{ flex: 1, display: "flex", flexDirection: "column" }}>
            <div
              style={{
                display: "flex",
                alignItems: "center",
                gap: "0.5rem",
                marginBottom: "0.5rem",
              }}
            >
              <input
                type="text"
                readOnly
                value={currentTimestamp}
                style={{
                  flexGrow: 1,
                  fontWeight: "bold",
                  fontSize: "1.2rem",
                  borderRadius: "6px",
                  border: "1px solid #ccc",
                  padding: "0.3rem 0.6rem",
                  backgroundColor: "#f9f9f9",
                  color: "#333",
                }}
              />
              <button
                type="button"
                onClick={handleRefreshTime}
                style={{
                  padding: "0.3rem 0.75rem",
                  fontSize: "1rem",
                  cursor: "pointer",
                  borderRadius: "6px",
                  border: "1px solid #888",
                  backgroundColor: "#f0f0f0",
                  color: "#333",
                  fontWeight: "bold",
                }}
              >
                Refresh Time
              </button>
            </div>

            <textarea
              rows={12}
              value={entry}
              onChange={(e) => setEntry(e.target.value)}
              placeholder="Type or dictate your journal entry..."
              style={{
                width: "100%",
                padding: "0.5rem",
                fontSize: "1rem",
                borderRadius: "6px",
                border: "1px solid #ccc",
                backgroundColor: "#fff",
                color: "#222",
                whiteSpace: "pre-wrap",
              }}
            />
          </div>
        </div>

        <button
          type="submit"
          style={{
            marginTop: "1.5rem",
            display: "block",
            marginLeft: "auto",
            marginRight: "auto",
            padding: "0.75rem 2rem",
            fontSize: "1rem",
            borderRadius: "6px",
            border: "none",
            cursor: "pointer",
            backgroundColor: "#4CAF50",
            color: "white",
            boxShadow: "0 3px 6px rgba(0,0,0,0.2)",
          }}
        >
          Save Entry
        </button>

        {saveMessage && (
          <div
            style={{
              marginTop: "1rem",
              textAlign: "center",
              padding: "0.75rem 1rem",
              backgroundColor: saveMessage.includes("Error")
                ? "#f8d7da"
                : "#d4edda",
              color: saveMessage.includes("Error") ? "#721c24" : "#155724",
              borderRadius: "4px",
              border: saveMessage.includes("Error")
                ? "1px solid #f5c6cb"
                : "1px solid #c3e6cb",
              fontWeight: "bold",
              maxWidth: "400px",
              marginLeft: "auto",
              marginRight: "auto",
              userSelect: "none",
            }}
          >
            {saveMessage}
          </div>
        )}
      </form>
    </div>
  );
}
