import React, { useState, useEffect, useRef } from "react";
import logo from "./assets/logo.png";

const PROXY_API_URL = "http://localhost:8090/transcribeAudio";

export default function Journal({ user, onLogout }) {
  // ... keep all your existing state and refs ...

  // We'll keep journal content as an array of strings (lines/paragraphs)
  const [journalLines, setJournalLines] = useState([]);

  // During recording, hide textarea, show diary
  // When transcription comes back, add to journalLines

  // When user types manually, update last paragraph or add new one — up to you.
  // For simplicity, let's just disable manual typing during recording.

  // Replace `entry` with `journalLines` in rendering

  // When transcription returns:
  const appendTranscription = (text) => {
    setJournalLines((lines) => [...lines, text]);
  };

  // Update your onstop handler to:
  // ...
  // if (data.transcription) appendTranscription(data.transcription);
  // ...

  // Manual typing (optional): Could add a "manual input" mode or append to last line,
  // but for now let's disable typing while recording.

  // When saving, concatenate all lines
  const getFullEntry = () => journalLines.join("\n\n");

  // On submit, send getFullEntry()

  // In render, replace textarea with this diary view when recording:
  // If recording: show diary container with lines
  // Else: show textarea with full text so user can edit before saving

  // Then the UI code for diary container:
  // scrollable div, styled with nice font, line spacing, etc.

  // ... rest of component remains the same

  // Here is the modified render part you asked for:

  return (
    <div style={{ minHeight: "100vh", backgroundColor: "#FFF8E7", padding: "2rem", maxWidth: 700, margin: "auto", fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif", color: "#222" }}>
      {/* ... header, logo, user info, etc ... */}

      <form onSubmit={async (e) => {
        e.preventDefault();
        if (getFullEntry().trim() === "") {
          setSaveMessage("Please enter something before saving.");
          setShowToast(true);
          return;
        }
        const result = await saveToGoogleSheet(currentTimestamp, getFullEntry(), user);
        if (result) {
          setSaveMessage("Journal entry saved successfully!");
          setShowToast(true);
          setJournalLines([]);  // clear on success
          setCurrentTimestamp(generateTimestamp());
        } else {
          setSaveMessage("Failed to save entry. Please try again.");
          setShowToast(true);
        }
      }}>
        <div style={{ display: "flex", gap: "1.5rem" }}>
          <div style={{ display: "flex", flexDirection: "column", gap: "1rem", flexShrink: 0, marginTop: "4rem" }}>
            <button
              type="button"
              onClick={handleRecordButtonClick}
              style={{
                backgroundColor: isRecording ? "#c0392b" : "#e74c3c",
                color: "white",
                fontSize: "1.5rem",
                width: "80px",
                height: "80px",
                border: "none",
                borderRadius: "50%",
                cursor: "pointer",
                boxShadow: isRecording ? "0 0 15px 5px rgba(192, 57, 43, 0.7)" : "0 0 15px 5px rgba(231, 76, 60, 0.7)",
              }}
              title={isRecording ? "Stop recording" : "Start recording"}
            >
              {isRecording ? "Stop" : "Record"}
            </button>
          </div>

          <div style={{ flex: 1, display: "flex", flexDirection: "column" }}>
            <div style={{ display: "flex", alignItems: "center", gap: "0.5rem", marginBottom: "0.5rem" }}>
              <input
                type="text"
                readOnly
                value={currentTimestamp}
                style={{
                  flexGrow: 1,
                  fontWeight: "bold",
                  fontSize: "1.2rem",
                  borderRadius: "6px",
                  border: "1px solid #ccc",
                  padding: "0.3rem 0.6rem",
                  backgroundColor: "#f9f9f9",
                  color: "#333",
                }}
              />
              <button
                type="button"
                onClick={handleRefreshTime}
                style={{
                  padding: "0.3rem 0.75rem",
                  fontSize: "1rem",
                  cursor: "pointer",
                  borderRadius: "6px",
                  border: "1px solid #888",
                  backgroundColor: "#f0f0f0",
                  color: "#333",
                  fontWeight: "bold",
                }}
              >
                Refresh Time
              </button>
            </div>

            {!isRecording ? (
              <textarea
                rows={12}
                value={getFullEntry()}
                onChange={(e) => setJournalLines(e.target.value.split("\n\n"))}
                placeholder="Type or dictate your journal entry..."
                style={{
                  width: "100%",
                  padding: "0.5rem",
                  fontSize: "1rem",
                  borderRadius: "6px",
                  border: "1px solid #ccc",
                  backgroundColor: "#fff",
                  color: "#222",
                  whiteSpace: "pre-wrap",
                  fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
                  resize: "vertical",
                }}
              />
            ) : (
              <div
                aria-live="polite"
                style={{
                  width: "100%",
                  minHeight: "240px",
                  padding: "1rem",
                  borderRadius: "6px",
                  border: "1px solid #ccc",
                  backgroundColor: "#fff8e1",
                  color: "#5b4636",
                  fontFamily: "'Indie Flower', cursive, 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
                  fontSize: "1.2rem",
                  lineHeight: 1.6,
                  overflowY: "auto",
                  boxShadow: "inset 0 0 10px #f0e6d2",
                  whiteSpace: "pre-wrap",
                  userSelect: "none",
                }}
              >
                {journalLines.length === 0
                  ? "Listening... your words will appear here as you speak."
                  : journalLines.map((line, i) => (
                      <p key={i} style={{ margin: "0 0 1em" }}>
                        {line}
                      </p>
                    ))}
              </div>
            )}
          </div>
        </div>

        <button
          type="submit"
          disabled={isRecording}
          style={{
            marginTop: "1.5rem",
            display: "block",
            marginLeft: "auto",
            marginRight: "auto",
            padding: "0.75rem 2rem",
            fontSize: "1rem",
            borderRadius: "6px",
            border: "none",
            cursor: isRecording ? "not-allowed" : "pointer",
            backgroundColor: isRecording ? "#999" : "#4CAF50",
            color: "white",
            boxShadow: "0 3px 6px rgba(0,0,0,0.2)",
          }}
          title={isRecording ? "Stop recording before saving" : "Save your journal entry"}
        >
          Save Entry
        </button>
      </form>
    </div>
  );
}
