import React, { useState, useEffect, useRef } from "react";
import logo from "./assets/logo.png";

const PROXY_API_URL = "http://localhost:8090/transcribeAudio";

export default function Journal({ user, onLogout }) {
  const [entry, setEntry] = useState("");
  const [isRecording, setIsRecording] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [saveMessage, setSaveMessage] = useState("");
  const [currentTimestamp, setCurrentTimestamp] = useState("");
  const [countdown, setCountdown] = useState(0);
  const [transcribedText, setTranscribedText] = useState("");
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const countdownIntervalRef = useRef(null);

  const generateTimestamp = () => {
    const now = new Date();
    return `ðŸ•’ ${now.toLocaleString()}`;
  };

  useEffect(() => {
    setCurrentTimestamp(generateTimestamp());
  }, []);

  const startTimedRecording = async (durationSeconds) => {
    if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
      setSaveMessage("Audio recording not supported in this browser.");
      setShowToast(true);
      return;
    }

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstart = () => {
        setIsRecording(true);
        setShowToast(true);
        setSaveMessage("Recording in progress...");
        setCountdown(durationSeconds);

        countdownIntervalRef.current = setInterval(() => {
          setCountdown((prev) => {
            if (prev <= 1) {
              stopRecording();
              return 0;
            }
            return prev - 1;
          });
        }, 1000);
      };

      mediaRecorder.onstop = async () => {
        clearInterval(countdownIntervalRef.current);
        setCountdown(0);
        setIsRecording(false);
        setSaveMessage("Processing audio...");
        setShowToast(true);

        const audioBlob = new Blob(audioChunksRef.current, { type: "audio/webm" });
        try {
          const formData = new FormData();
          formData.append("file", audioBlob, "recording.webm");
          formData.append("user", user);

          const response = await fetch(PROXY_API_URL, {
            method: "POST",
            body: formData,
          });

          if (!response.ok) {
            throw new Error(`Transcription failed: ${response.statusText}`);
          }

          const data = await response.json();

          if (data.transcription) {
            setTranscribedText(data.transcription);
            await saveToGoogleSheet(generateTimestamp(), data.transcription, user);
            setSaveMessage("Transcription saved!");
          } else {
            setSaveMessage("No transcription received.");
          }
        } catch (error) {
          setSaveMessage("Error during transcription: " + error.message);
        }
      };

      mediaRecorder.start();
    } catch (err) {
      setSaveMessage("Could not start recording: " + err.message);
      setShowToast(true);
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state !== "inactive") {
      mediaRecorderRef.current.stop();
    }
    clearInterval(countdownIntervalRef.current);
    setIsRecording(false);
  };

  const saveToGoogleSheet = async (timestamp, text, username) => {
    try {
      const response = await fetch("http://localhost:8090/saveEntry", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ timestamp, entry: text, user: username }),
      });
      if (!response.ok) throw new Error("Failed to save entry to Google Sheets");
      return await response.json();
    } catch (error) {
      setSaveMessage("Error saving to Google Sheets: " + error.message);
      setShowToast(true);
      return null;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!entry.trim()) {
      setSaveMessage("Please enter something before saving.");
      setShowToast(true);
      return;
    }
    const result = await saveToGoogleSheet(currentTimestamp, entry, user);
    if (result) {
      setSaveMessage("Journal entry saved successfully!");
      setShowToast(true);
      setEntry("");
      setCurrentTimestamp(generateTimestamp());
    } else {
      setSaveMessage("Failed to save entry. Please try again.");
      setShowToast(true);
    }
  };

  useEffect(() => {
    return () => {
      if (mediaRecorderRef.current && mediaRecorderRef.current.state !== "inactive") {
        mediaRecorderRef.current.stop();
      }
      clearInterval(countdownIntervalRef.current);
    };
  }, []);

  useEffect(() => {
    if (saveMessage) {
      const timer = setTimeout(() => setSaveMessage(""), 4000);
      return () => clearTimeout(timer);
    }
  }, [saveMessage]);

  const countdownPercent = ((countdown === 0 ? 0 : 180 - countdown) / 180) * 100;

  return (
    <div
      style={{
        minHeight: "100vh",
        backgroundColor: "#FFF8E7",
        padding: "2rem",
        fontFamily: "sans-serif",
        maxWidth: 700,
        margin: "auto",
        color: "#222",
      }}
    >
      {isRecording && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "16px",
            backgroundColor: "#d0f0d0",
            zIndex: 9999,
          }}
        >
          <div
            style={{
              width: `${countdownPercent}%`,
              height: "100%",
              background: "linear-gradient(90deg, #2ecc71, #27ae60)",
              transition: "width 1s linear",
            }}
          />
        </div>
      )}

      {showToast && (
        <div
          style={{
            position: "fixed",
            top: "40px",
            left: "50%",
            transform: "translateX(-50%)",
            backgroundColor: "#333",
            color: "white",
            padding: "10px 20px",
            borderRadius: "20px",
            boxShadow: "0 2px 10px rgba(0,0,0,0.7)",
            zIndex: 9999,
            fontSize: "1rem",
            opacity: 0.9,
          }}
        >
          {saveMessage || "Recording in progress..."}
        </div>
      )}

      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          marginBottom: "1rem",
        }}
      >
        <img
          src={logo}
          alt="Logo"
          style={{ maxWidth: "150px", height: "auto" }}
        />
        <div>
          <strong>User:</strong> {user}
          <button
            onClick={onLogout}
            style={{
              marginLeft: "1rem",
              padding: "0.3rem 0.75rem",
              fontSize: "1rem",
              cursor: "pointer",
              borderRadius: "6px",
              border: "1px solid #888",
              backgroundColor: "#f0f0f0",
            }}
          >
            Logout
          </button>
        </div>
      </div>

      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: "1rem" }}>
          <button
            type="button"
            onClick={() => startTimedRecording(60)}
            style={{
              marginRight: "1rem",
              padding: "0.5rem 1rem",
              backgroundColor: "#3498db",
              color: "white",
              border: "none",
              borderRadius: "6px",
              fontSize: "1rem",
              cursor: "pointer",
            }}
          >
            1 min
          </button>
          <button
            type="button"
            onClick={() => startTimedRecording(180)}
            style={{
              padding: "0.5rem 1rem",
              backgroundColor: "#9b59b6",
              color: "white",
              border: "none",
              borderRadius: "6px",
              fontSize: "1rem",
              cursor: "pointer",
            }}
          >
            3 min
          </button>
        </div>

        {transcribedText && (
          <div
            style={{
              margin: "1rem 0",
              padding: "1rem",
              backgroundColor: "#fefefe",
              borderLeft: "4px solid #4CAF50",
              borderRadius: "6px",
              fontSize: "1rem",
              whiteSpace: "pre-wrap",
            }}
          >
            {transcribedText}
          </div>
        )}

        <div style={{ marginTop: "1rem" }}>
          <textarea
            rows={10}
            value={entry}
            onChange={(e) => setEntry(e.target.value)}
            placeholder="Type your journal entry manually here..."
            style={{
              width: "100%",
              padding: "0.5rem",
              fontSize: "1rem",
              borderRadius: "6px",
              border: "1px solid #ccc",
              backgroundColor: "#fff",
              color: "#222",
              whiteSpace: "pre-wrap",
            }}
          />
        </div>

        <button
          type="submit"
          style={{
            marginTop: "1.5rem",
            display: "block",
            marginLeft: "auto",
            marginRight: "auto",
            padding: "0.75rem 2rem",
            fontSize: "1rem",
            borderRadius: "6px",
            border: "none",
            cursor: "pointer",
            backgroundColor: "#4CAF50",
            color: "white",
            boxShadow: "0 3px 6px rgba(0,0,0,0.2)",
          }}
        >
          Save Entry
        </button>
      </form>
    </div>
  );
}
